import org.as3collections.IList;
import org.as3collections.IListIterator;
import org.as3collections.lists.ArrayList;

var list1:IList = new ArrayList([1, 3, 5]);

list1                             // [1,3,5]

var it:IListIterator = list1.listIterator();
var e:int;

while (it.hasNext())
{

    ITERATION N.1

    it.pointer()                  // -1
    it.nextIndex()                // 0
    it.previousIndex()            // -1

    e = it.next();
    e                             // 1

    it.pointer()                  // 0
    it.nextIndex()                // 1
    it.previousIndex()            // 0

    ITERATION N.2

    it.pointer()                  // 0
    it.nextIndex()                // 1
    it.previousIndex()            // 0

    e = it.next();
    e                             // 3

    it.pointer()                  // 1
    it.nextIndex()                // 2
    it.previousIndex()            // 1

    if (e == 3)
    {
        //list1.add(4)            // ConcurrentModificationError: During the iteration, the list was changed directly (without use the iterator).
        it.add(4);
        list1                     // [1,3,4,5]
    }

    ITERATION N.3

    it.pointer()                  // 2
    it.nextIndex()                // 3
    it.previousIndex()            // 2

    e = it.next();
    e                             // 5

    it.pointer()                  // 3
    it.nextIndex()                // 4
    it.previousIndex()            // 3

    if (e == 5)
    {
        it.remove();
        list1                     // [1,3,4]
    }
}

import org.as3collections.IList;
import org.as3collections.IListIterator;
import org.as3collections.lists.ArrayList;

var list1:IList = new ArrayList([1, 3, 5]);

list1                             // [1,3,5]

var it:IListIterator = list1.listIterator(list1.size());
var e:int;

while (it.hasPrevious())

{

    ITERATION N.1

    it.pointer()                  // 2
    it.nextIndex()                // 3
    it.previousIndex()            // 2

    e = it.previous();
    e                             // 5

    it.pointer()                  // 1
    it.nextIndex()                // 2
    it.previousIndex()            // 1

    if (e == 5)
    {
        it.remove()
        list1                     // [1,3]
    }

    ITERATION N.2

    it.pointer()                  // 1
    it.nextIndex()                // 2
    it.previousIndex()            // 1

    e = it.previous();
    e                             // 3

    it.pointer()                  // 0
    it.nextIndex()                // 1
    it.previousIndex()            // 0

    if (e == 3)
    {
        //list1.add(4)            // ConcurrentModificationError: During the iteration, the list was changed directly (without use the iterator).
        it.add(4);
        list1                     // [1,4,3]
    }

    ITERATION N.3

    it.pointer()                  // 1
    it.nextIndex()                // 2
    it.previousIndex()            // 1

    e = it.previous();
    e                             // 4

    it.pointer()                  // 0
    it.nextIndex()                // 1
    it.previousIndex()            // 0

    ITERATION N.4

    it.pointer()                  // 0
    it.nextIndex()                // 1
    it.previousIndex()            // 0

    e = it.previous();
    e                             // 1

    it.pointer()                  // -1
    it.nextIndex()                // 0
    it.previousIndex()            // -1
}

import org.as3collections.IIterator;
import org.as3collections.IMap;
import org.as3collections.maps.ArrayListMap;

var map1:IMap = new ArrayListMap();
map1.put("element-1", 1);
map1.put("element-3", 3);
map1.put("element-5", 5);
map1.put("element-7", 7);

map1                             // ["element-1"=1,"element-3"=3,"element-5"=5,"element-7"=7]

var it:IIterator = map1.iterator();
var e:int;

while (it.hasNext())
{
    ITERATION N.1

    it.pointer()                  // null

    e = it.next();
    e                             // 1

    it.pointer()                  // "element-1"

    ITERATION N.2

    it.pointer()                  // "element-1"

    e = it.next();
    e                             // 3

    it.pointer()                  // "element-3"

    if (e == 3)
    {
        it.remove();
        map1                      // ["element-1"=1,"element-5"=5,"element-7"=7]
    }

    ITERATION N.3

    it.pointer()                  // "element-1"

    e = it.next();
    e                             // 5

    it.pointer()                  // "element-5"

    ITERATION N.4

    it.pointer()                  // "element-5"

    e = it.next();
    e                             // 7

    it.pointer()                  // "element-7"
}

import org.as3collections.IIterator;
import org.as3collections.IList;
import org.as3collections.lists.ArrayList;

var list1:IList = new ArrayList([1, 3, 5, 7]);

list1                             // [1,3,5,7]

var it:IIterator = list1.iterator();
var e:int;

while (it.hasNext())
{
    ITERATION N.1

    it.pointer()                  // -1

    e = it.next();
    e                             // 1

    it.pointer()                  // 0

    ITERATION N.2

    it.pointer()                  // 0

    e = it.next();
    e                             // 3

    it.pointer()                  // 1

    if (e == 3)
    {
        it.remove();
        list1                     // [1,5,7]
    }

    ITERATION N.3

    it.pointer()                  // 0

    e = it.next();
    e                             // 5

    it.pointer()                  // 1

    ITERATION N.4

    it.pointer()                  // 1

    e = it.next();
    e                             // 7

    it.pointer()                  // 2
}

import org.as3collections.IListMap;
import org.as3collections.IListMapIterator;
import org.as3collections.maps.ArrayListMap;

var map1:IListMap = new ArrayListMap();
map1.put("element-1", 1);
map1.put("element-3", 3);
map1.put("element-5", 5);

map1                                // ["element-1"=1,"element-3"=3,"element-5"=5]

var it:IListMapIterator = map1.listMapIterator();
var e:int;

while (it.hasNext())
{

    ITERATION N.1

    it.pointer()                    // null
    it.nextIndex()                  // 0
    it.previousIndex()              // -1

    e = it.next();
    e                               // 1

    it.pointer()                    // "element-1"
    it.nextIndex()                  // 1
    it.previousIndex()              // 0

    ITERATION N.2

    it.pointer()                    // "element-1"
    it.nextIndex()                  // 1
    it.previousIndex()              // 0

    e = it.next();
    e                               // 3

    it.pointer()                    // "element-3"
    it.nextIndex()                  // 2
    it.previousIndex()              // 1

    if (e == 3)
    {
        //map1.put("element-4", 4)  // ConcurrentModificationError: During the iteration, the map was changed directly (without use the iterator).
        it.put("element-4", 4);
        map1                        // ["element-1"=1,"element-3"=3,"element-4"=4,"element-5"=5]
    }

    ITERATION N.3

    it.pointer()                    // "element-4"
    it.nextIndex()                  // 3
    it.previousIndex()              // 2

    e = it.next();
    e                               // 5

    it.pointer()                    // "element-5"
    it.nextIndex()                  // 4
    it.previousIndex()              // 3

    if (e == 5)
    {
        it.remove();
        map1                        // ["element-1"=1,"element-3"=3,"element-4"=4]
    }
}

import org.as3collections.IListMap;
import org.as3collections.IListMapIterator;
import org.as3collections.maps.ArrayListMap;

var map1:IListMap = new ArrayListMap();
map1.put("element-1", 1);
map1.put("element-3", 3);
map1.put("element-5", 5);

map1                                // ["element-1"=1,"element-3"=3,"element-5"=5]

var it:IListMapIterator = map1.listIterator(map1.size());
var e:int;

while (it.hasPrevious())

{

    ITERATION N.1

    it.pointer()                    // "element-5"
    it.nextIndex()                  // 3
    it.previousIndex()              // 2

    e = it.previous();
    e                               // 5

    it.pointer()                    // "element-3"
    it.nextIndex()                  // 2
    it.previousIndex()              // 1

    if (e == 5)
    {
        it.remove()
        map1                        // ["element-1"=1,"element-3"=3]
    }

    ITERATION N.2

    it.pointer()                    // "element-3"
    it.nextIndex()                  // 2
    it.previousIndex()              // 1

    e = it.previous();
    e                               // 3

    it.pointer()                    // "element-1"
    it.nextIndex()                  // 1
    it.previousIndex()              // 0

    if (e == 3)
    {
        //map1.put("element-4", 4); // ConcurrentModificationError: During the iteration, the map was changed directly (without use the iterator).
        it.put("element-4", 4);
        map1                        // [1,4,3]
    }

    ITERATION N.3

    it.pointer()                    // "element-3"
    it.nextIndex()                  // 2
    it.previousIndex()              // 1

    e = it.previous();
    e                               // 4

    it.pointer()                    // "element-1"
    it.nextIndex()                  // 1
    it.previousIndex()              // 0

    ITERATION N.4

    it.pointer()                    // "element-1"
    it.nextIndex()                  // 1
    it.previousIndex()              // 0

    e = it.previous();
    e                               // 1

    it.pointer()                    // null
    it.nextIndex()                  // 0
    it.previousIndex()              // -1
}

import org.as3collections.IList;
import org.as3collections.IListIterator;
import org.as3collections.lists.ArrayList;
import org.as3collections.lists.UniqueList;
import org.as3collections.utils.ListUtil;

var l1:IList = new ArrayList([3, 5, 1, 7]);

var list1:IList = new UniqueList(l1); // you can use this way

//var list1:IList = ListUtil.getUniqueList(l1); // or you can use this way

list1                       // [3,5,1,7]
list1.size()                // 4

list1.addAt(1, 4)           // true
list1                       // [3,4,5,1,7]
list1.size()                // 5

list1.addAt(2, 3)           // false
list1                       // [3,4,5,1,7]
list1.size()                // 5

list1.add(5)                // false
list1                       // [3,4,5,1,7]
list1.size()                // 5

var it:IListIterator = list1.listIterator();
var e:int;

while (it.hasNext())
{

    e = it.next()
    e                       // 3

    e = it.next()
    e                       // 4

    e = it.next()
    e                       // 5

    if (e == 5)
    {
        it.add(0)

        list1               // [3,4,5,0,1,7]
        list1.size()        // 6
    }

    e = it.next()
    e                       // 1

    if (e == 1)
    {
        it.add(3)

        list1               // [3,4,5,0,1,7]
        list1.size()        // 6
    }

    e = it.next()
    e                       // 7
}

list1                       // [3,4,5,0,1,7]
list1.size()                // 6

var l2:IList = new ArrayList([1, 2, 3, 4, 5, 1, 3, 5]);

var list2:IList = new UniqueList(l2); // you can use this way

//var list2:IList = ListUtil.getUniqueList(l2); // or you can use this way

list2                       // [1,2,3,4,5]
list2.size()                // 5

