var it:IListIterator = list1.listIterator();
var e:int;

while (it.hasNext())
{

    e = it.next()
    e                          // 3

    e = it.next()
    e:                         // 4

    e = it.next()
    e                          // 5

    if (e == 5)
    {
        it.add(0)

        list1                  // [3,4,5,0,1,7,8]
        list1.size()           // 7
    }

    e = it.next()
    e                          // 1

    if (e == 1)
    {
        it.add(3)

        list1                  // [3,4,5,0,1,3,7,8]
        list1.size()           // 8
    }

    e = it.next()
    e                          // 7

    e = it.next()
    e                          // 8

    if (e == 8)
